function ready(e){"loading"!=document.readyState?e():document.addEventListener("DOMContentLoaded",e)}function removeActive(){for(var e=nav.length-1;e>=0;e--)nav[e].classList.remove("active")}function hideAll(){for(var e=document.querySelectorAll("section"),t=0;t<e.length;t++)e[t].classList.add("hidden");removeActive()}function showProject(){for(var e=document.querySelectorAll(".project-tabs li"),t=document.querySelectorAll(".proj"),a=e.length-1;a>=0;a--)e[a].addEventListener("click",function(){for(var a=t.length-1;a>=0;a--)t[a].classList.add("hidden");[].forEach.call(e,function(e){e.classList.remove("active-tab")});var n=this.className,c=document.querySelector("."+n+"-content");this.classList.add("active-tab"),c.classList.remove("hidden")})}function calculateYears(){var e=new Date(1998,1,10),t=Date.now()-e.getTime(),a=new Date(t);return Math.abs(a.getUTCFullYear()-1970)}var nav;ready(function(){nav=document.querySelectorAll("nav ul li a"),showProject();for(var e=nav.length-1;e>=0;e--)nav[e].addEventListener("click",function(){hideAll();var e=this.textContent.toLowerCase();this.classList.contains(e)&&(this.classList.add("active"),document.querySelector("#"+e).classList.remove("hidden"))});document.querySelector(".years").textContent=calculateYears()});