function ready(e){"loading"!=document.readyState?e():document.addEventListener("DOMContentLoaded",e)}function removeActive(){for(var e=nav.length-1;e>=0;e--)nav[e].classList.remove("active")}function hideAll(){for(var e=document.querySelectorAll("section"),t=0;t<e.length;t++)e[t].classList.add("hidden");removeActive()}function showProject(){for(var e=document.querySelectorAll(".project-tabs li"),t=document.querySelectorAll(".proj"),n=e.length-1;n>=0;n--)e[n].addEventListener("click",function(){for(var e=t.length-1;e>=0;e--)t[e].classList.add("hidden");var n=this.className;document.querySelector("."+n+"-content").classList.remove("hidden")})}var nav;ready(function(){nav=document.querySelectorAll("nav ul li a"),showProject();for(var e=nav.length-1;e>=0;e--)nav[e].addEventListener("click",function(){hideAll();var e=this.textContent.toLowerCase();this.classList.contains(e)&&(this.classList.add("active"),document.querySelector("#"+e).classList.remove("hidden"))})});