function ready(e){"loading"!=document.readyState?e():document.addEventListener("DOMContentLoaded",e)}function removeActive(){for(var e=nav.length-1;e>=0;e--)nav[e].classList.remove("active")}function hideAll(){for(var e=document.querySelectorAll("section"),t=e.length-1;t>=0;t--)e[t].classList.add("hidden");removeActive()}var nav;ready(function(){var e=document.querySelector(".one"),t=document.querySelector(".two"),n=document.querySelector(".three"),a=document.querySelector(".four");nav=document.querySelectorAll("nav ul li a");for(var c=nav.length-1;c>=0;c--)nav[c].addEventListener("click",function(){hideAll(),this.classList.contains("about")?(e.classList.remove("hidden"),this.classList.add("active")):this.classList.contains("projects")&&(t.classList.remove("hidden"),this.classList.add("active"))})});